
from ipdb import set_trace
from typing import List

class Solution:
    def reconstructQueue(self, people: List[List[int]]) -> List[List[int]]:

        people = sorted(people, key=lambda x: (-x[0], x[1]))
        queue = []
        for person in people:
            set_trace()
            queue.insert(person[1], person)

        return queue

        # people = sorted(people,key=lambda x:(x[0]*-1,x[1]))
        # ans = []
        # for p in people:
        #     ans.insert(p[1],p)
        # return ans

    def reconstructQueueLong(self, people: List[List[int]]) -> List[List[int]]:

        if people == []:
            return []

        queue = [x for x in people if x[1] == 0]
        hmin = min([x[0] for x in queue])
        queue = [x for x in queue if x[0] == hmin]
        for person in queue:
            people.remove(person)
        while len(people) > 0:

            # find number in front with equal or greater height
            num = []
            for person in people:
                num.append(len([x for x in queue if x[0] >= person[0]]))

            # find people that can enter the queue
            ok = [x for x, y in zip(people, num) if y >= x[1]]

            # put min height people in the queue
            hmin = min([x[0] for x in ok])
            ok = [x for x in ok if x[0] == hmin]
            for person in ok:
                queue.append(person)
                people.remove(person)

        return queue

sol = Solution()
assert sol.reconstructQueue([[7,0], [4,4], [7,1], [5,0], [6,1], [5,2]]) == [[5,0], [7,0], [5,2], [6,1], [4,4], [7,1]]
